INSERT INTO `modalidades` VALUES (1,'TD','Trabajo Dirigido', 'Consiste en trabajos prácticos evaluados y supervisados en instituciones, empresas públicas o privadas, encargadas de proyectar o implementar obras para lo cual y sobre la base de un temario se proyecta, dirige o fiscaliza bajo la dirección de un supervisor o guía de la institución o empresa, también otro campo de acción es el de verificar las soluciones de problemas específicos, demostrando dominio amplio del tema y capacidad para resolver.',1, NULL, NULL, NULL);
INSERT INTO `modalidades` VALUES (2,'PG','Proyecto de Grado', 'Es el trabajo de investigación, análisis y diseño de objetos de fin social y que cumple con exigencias de metodología cientifica con profundidad similar al de un proyecto de investigación (tesis).',1, NULL, NULL, NULL);
INSERT INTO `modalidades` VALUES (3,'AD','Adscripcion', 'Se establece la Adscripción como la incorporación de estudiantes de la UMSS a la realización de actividades en los ámbitos académico, de investigación, interacción, y/o de gestión universitaria. La Adscripción consiste en la realización de un Trabajo Dirigido y/o una práctica profesional supervisada (internado) dentro de la Universidad Mayor de San Simón, que desarrollado dentro del marco de las disposiciones del presente reglamento, habilita al estudiante a presentar como Proyecto Final.Para las Carreras de Informática y Sistemas, se plantea clasificar esta modalidad en dos:Adscripción a la Catedra,si las actividades a realizar persiguen fines netamente académicos y de investigación enmarcadas en el proceso enseñanza-aprendizaje de las carreras de Informática y Sistemas.Adscripción,si las actividades a realizar no están enmarcadas en el ámbito de la Adscripción a la Cátedra.',1, NULL, NULL, NULL);
INSERT INTO `modalidades` VALUES (4,'PI','Proyecto de Investigacion (Tesis)', 'Es un trabajo de investigación, que cumple con exigencias de metodología científica, a objeto de conocer y dar respuestas a un problema, planteando alternativas aplicables o proponiendo soluciones prácticas y/o teóricas.',1, NULL, NULL, NULL);
INSERT INTO `modalidades` VALUES (5,'EX','Excelencia', '' ,1, NULL, NULL, NULL);
